import java.util.Scanner;

public class Main {
    public static void main(String[] args) {

        Enemy enemy;
        Scanner scanner = new Scanner(System.in);
        EnemyFactory factory = new EnemyFactory();

        System.out.println("Enter 'A' for Commander or any other key for Skeleton:");

        // If there is input from the user
        if (scanner.hasNextLine()) {
            String input = scanner.nextLine();
            enemy = factory.createEnemies(input);  // Create enemy using the factory

            // Display enemy details if created
            if (enemy != null) {
                enemy.showEnemy();  // Call the method to show enemy details
            } else {
                System.out.println("No enemy created.");
            }
        }

        scanner.close();  // Close the scanner after usage
    }
}
