package Modulo1.Aula25.Factory;

import java.util.Scanner;

public class Main {

    public static void main(String[] args) {

        Enemy enemy;
        Scanner scanner = new Scanner(System.in);
        EnemyFactory factory = new EnemyFactory();

        System.out.println("Enter 'A' to create a Commander or any other key to create a Skeleton:");

        // Check if the user has input a line and process it
        if(scanner.hasNextLine()){
            String input = scanner.nextLine();
            enemy = factory.createEnemies(input);

            // Display enemy details
            enemy.showEnemy();
        }
        scanner.close();
    }
}
