import java.util.Scanner;

public class FindLargestNumber {
    public static void main(String[] args) {
        maiorValor();
    }

    public static void maiorValor() {
        Scanner in = new Scanner(System.in);
        System.out.println("Digite três números: ");

        System.out.print("Nº1? ");
        int num1 = in.nextInt();
        System.out.print("Nº2? ");
        int num2 = in.nextInt();
        System.out.print("Nº3? ");
        int num3 = in.nextInt();

        if (num1 >= num2 && num1 >= num3) {
            System.out.println("O número maior é " + num1);
        } else if (num2 >= num1 && num2 >= num3) {
            System.out.println("O número maior é " + num2);
        } else {
            System.out.println("O número maior é " + num3);
        }

        // Close the scanner
        in.close();
    }
}
