public class RoundToNearestInteger {
    public static void main(String[] args) {
        System.out.println(arredondarParaInteiros(8.8));  // Saída será 9
        System.out.println(arredondarParaInteiros(8.2));  // Saída será 8
    }

    public static int arredondarParaInteiros(double numero) {
        double casaDecimal = numero - (int)numero;

        if (casaDecimal < 0.5) {
            return (int) numero;  // Arredonda para baixo
        } else {
            return (int) numero + 1;  // Arredonda para cima
        }
    }
}
