package TrimThroughText;

import java.io.File;
import java.io.FileNotFoundException;
import java.util.ArrayList;
import java.util.List;
import java.util.Scanner;

public class TrimAttributes {

    public class TrimAttributes {

        private String name;
        private String address;
        private String email;

        // Constructor
        public TrimAttributes(String name, String address, String email) {
            this.name = name;
            this.address = address;
            this.email = email;
        }

        // Method to trim all string attributes
        public void trimAllAttributes() {
            if (name != null)
                name = name.trim();
            if (address != null)
                address = address.trim();
            if (email != null)
                email = email.trim();
        }

        @Override
        public String toString() {
            return "TrimAttributes{" +
                    "name='" + name + '\'' +
                    ", address='" + address + '\'' +
                    ", email='" + email + '\'' +
                    '}';
        }

        public static void main(String[] args) {
            TrimAttributes person = new TrimAttributes("  Alice  ", "  Wonderland  ", " alice@example.com   ");
            System.out.println("Before Trimming: " + person);

            person.trimAllAttributes();
            System.out.println("After Trimming: " + person);
        }
    }

}
