        if (temp < 0) {
            System.out.println("Está gelado");
        } else if (temp < 15) {
            System.out.println("Está frio");
        } else if (temp > 50) {
            System.out.println("Está quente");
        } else if (temp > 80) {
            System.out.println("Está a ferver");
        } else {
            System.out.println("Está normal");
        }
    }
}
