//<---  Function to see in a Array how many times the biggest value appears --->

function contarVelasMaisAltas(velas) {
    let max = -Infinity;
    let count = 0;

    for (let i = 0; i < velas.length; i++) {
        if (velas[i] > max) {
            max = velas[i];
            count = 1;
        } else if (velas[i] === max) {
            count++;
        }
    }
    return count;
}
console.log(contarVelasMaisAltas([1, 2, 8, 2, 5, 8]));
