function verificarParOuImpar(numero) {
    // Check if the number is divisible by 2
    if (numero % 2 === 0) {
        return "Par"; // Return "Par" if the number is even
    } else {
        return "Ímpar"; // Return "Ímpar" if the number is odd
    }
}

// Example usage:
console.log(verificarParOuImpar(4));  // Output: "Par"
console.log(verificarParOuImpar(7));  // Output: "Ímpar"
console.log(verificarParOuImpar(0));  // Output: "Par" (0 is considered even)
